script:
const get_milestone_schemes = async() => {
    return frappe.call({
        method: "myojana.api.top_schemes",
        callback:(r) => {
            if (r.message) {
                return r.message;
            }
        },
    });
}

const render_table = async()=>{
    const response = await get_milestone_schemes()
    let milestones = response.message;
    console.log("milestones",milestones)
    let tblHead = `
        <tr>
            <th scope="col"></th>
            <th scope="col">Scheme</th>
            <th scope="col"> Eligible Beneficiaries</th>
        </tr>
    `
    let rows = [];
    for(let milestone of milestones){
        if(!milestone.schemes?.length){
            continue;
        }
        rows.push(`
            <tr class="h5 border-top border-left border-right border-bottom">
                <td style="text-align:center;" colspan="3">${milestone.name}</td>
            </tr>
        `)

        for(const [index, scheme] of milestone.schemes.entries()){
            rows.push(`
                <tr class="border-left border-right">
                    <td scope="col">${(index +1)}</td>
                    <td scope="col"><a href="/app/scheme/${scheme.name}">${scheme.name}</a></td>
                    <td scope="col">${scheme.ben_count}</td>
                </tr>
            `)  
        }
        rows.push(`
            <tr class="h5 border-top">
                <td colspan="3">\n</td>
            </tr>
        `)
    }
    let table = `
        <table class="table table-bordered">
            ${tblHead}
            ${rows.join('\n')}
        </table>
    `;
    root_element.getElementById('table').innerHTML = table;

}


render_table()

