css:
body{
    margin:0;
    padding:0;
    box-sizing:border-box;
}
#container{
    display:flex;
    justify-content:center;
}
.id-card{
    min-height:240px;
    max-height:280px;
    border-radius:10px;
    width:430px;
    border:1px solid gray;
    padding:0px 10px 5px 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.5);
}
#org-logo{
    width:70px;
    padding:5px;
    height:60px;
    object-fit:contain;
}
.parent{
    width:100%;
    height:50%;
    display:flex;
    justify-content:center;
    align-items:center;
    gap:5px;
}
#left-block{
    height:100%;
    width:20%;
    display:flex;
    justify-content:end;
    align-items:center;
}
#right-block{
    height:100%;
    width:70%;
    padding:1px;
    line-height:12px;
    display:flex;
    align-items:start;
    font-size:12px;
}
.user-photo{
    height:100px;
    width:80px;
    border-radius:10%;
    object-fit:cover;
    border:3px solid #C28413;
}
#sign-block{
    width:100%;
    position:relative;
    display:flex;
    flex-direction:column;
    align-items:end;
    justify-content:end;
}
.signature{
    width:140px;
    height:40px;
    position:absolute;
    z-index:1000;
    bottom:5px;
    right:0px;
    object-fit:contain;
    mix-blend-mode: darken;
}
